import React, { useState } from 'react'
import "./styleFile.css";
import Icon from './icon-error.svg'
import ErrorIcon from './ErrorIcon';


const App = validate => {
  const [values, setValues] = useState({
    firstname: "",
    lastName: "",
    email:"",
    password:"" 
  });

  const [submitted, setSubmitted] = useState(false);
  const [valid, setValid] = useState(false);
  const [error, SetError] = useState({});

  function handleChange(e){
    const {name, value} = e.target;
    setValues({...values, 
      [name]: value
    })
  };



  const handleSubmit = event => {
    event.preventDefault();
    if(values.firstname && values.lastname && values.email){
      setValid(true)
    }
    setSubmitted(true);
  } 

  if(submitted && !values.email){
    <span>email is Required <ErrorIcon /></span>
  }else if(submitted && !values.email.includes('@')){
    <span>Invalid Email</span>
    }else {
    null
  }


  return (
    <div className='container'>

      <div className='text'>
      {/* <h1>Learn to Code by watching others </h1> */}
      {submitted && valid ? <h2>Thank you for Submitting your Data</h2>: null} 
      <p>
        See how experienced developers solve problems in real-time.
        Watching scripted tutorials is great, but understanding how
        developers think is invaluable.
      </p>
      </div>

      <div>
      <form onSubmit={handleSubmit}>  
       <div>
        <label>
          <input 
            type='text'
            name='firstname' 
            placeholder='First Name'
            className='input-field'
            value = {values.firstname}
            onChange={handleChange}
            />  
            <i>
            {submitted && !values.firstname ? <span>First Name is Required <ErrorIcon /></span> : null }

            {/* {submitted && !values.firstname ? setValues({...values, firstname: }): null} */}

            </i> 
        </label>
       </div> 

      <div>
      <label>           
          <input 
          type='text'
          name='lastname'
          className='input-field' 
          placeholder='Last Name'
          value = {values.lastname}
          onChange={handleChange}
          />
          <i>
        {submitted && !values.lastname ?  <span>Last name cannot be empty <ErrorIcon /></span>  : null } 
          </i>
        </label>
      </div>

      <div>
      <label>
          <input 
          type='text'
          name='email'
          className='input-field' 
          placeholder='Email Address'
          value = {values.email}
          onChange={handleChange} />

        <i>

        {/* {submitted && !values.email ? <span>email is Required <ErrorIcon /></span> : null }
        {submitted && !values.email.includes('@') ? <span>Invalid Email</span>: null} */}






        </i>
    </label>
      </div>

      <div>
      <label>
          <input 
          type='password'
          name='password'
          className='input-field' 
          placeholder='Passsword'
          value = {values.password}
          onChange={handleChange} />
        <i>
        {submitted && !values.password ?  <span>Password cannot be empty <ErrorIcon /></span> : null } 
        </i>
        </label>
      </div>

       <button type='submit'>Claim your free trial </button>
       <p>
         By clicking the button you are agreeing to our <strong>Terms and Servives</strong>
       </p>

      </form>

      </div>

    </div>
  )
}
export default App
