import React, { useState } from 'react'
import "./styleFile.css";


const App = validate => {
  const [values, setValues] = useState({
    firstname: "",
    lastName: "",
    email:"",
    password:"" 
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, SetError] = useState({});

  function handleChange(e){
    const {name, value} = e.target;
    setValues({...values, 
      [name]: value
    })
  };



  const handleSubmit = event => {
    event.preventDefault();

  } 


  // let emailMsg;
  // if (submitted && !values.email) {
  //   emailMsg = 'Email can not be empty';
  // }else if(submitted && !values.email.includes('@')){
  //   emailMsg = 'Email in not valid'
  // }
  // else {
  //   emailMsg = null
  // }

  return (
    <div className='container'>

      <div className='text'>
      <h1>Learn to Code by watching others </h1>
      <p>
        See how experienced developers solve problems in real-time.
        Watching scripted tutorials is great, but understanding how
        developers think is invaluable.
      </p>
      </div>

      <div>
      <form onSubmit={handleSubmit}>  
       <div>
        <label>
          <input 
            type='text'
            name='firstname' 
            placeholder='First Name'
            className='input-field'
            value = {values.firstname}
            onChange={handleChange}
            />  
            <i>
             {error.firstname}
            </i> 
        </label>
       </div> 



      <div>
      <label>           
          <input 
          type='text'
          name='lastname'
          className='input-field' 
          placeholder='Last Name'
          value = {values.lastname}
          onChange={handleChange}
          />
          <i>
        {/* {submitted && !values.lastName ?  <span>Last name cannot be empty</span>  : null }  */}

          </i>
        </label>
      </div>

      <div>
      <label>
          <input 
          type='text'
          name='email'
          className='input-field' 
          placeholder='Email Address'
          value = {values.email}
          onChange={handleChange} />

       {/* {<i><span>{emailMsg }</span> </i>} */}
    </label>



      </div>

      <div>
      <label>
          <input 
          type='password'
          name='password'
          className='input-field' 
          placeholder='Passsword'
          value = {values.password}
          onChange={handleChange} />
        <i>
        {/* {submitted && !values.password ?  <span>Password cannot be empty</span> : null }  */}
        </i>
        </label>
      </div>

       <button type='submit'>Claim your free trial </button>
       <p>
         By clicking the button you are agreeing to our <strong>Terms and Servives</strong>
       </p>
      </form>

      </div>
      {/* {submitted ? <h2>Thank you for Submitting your Data</h2>: null}  */}

    </div>
  )
}

export default App
