import style from 'styled-components'

export const Container = style.div`
    display: flex;
    align-items: center;
    justify-content: space-around;
    flex-wrap:wrap;
    height: 100vh;
    width:80%;
    margin:auto;
    background:coral;
`;

export const Div = style.div`
    width:500px;
` 

export const Button = style.button`
    background: blue;
    width:100%;
    margin-bottom:15px;
    padding:1.24rem;
    border:none;
    border-radius:5px;
    box-shadow:0px 5px 0px 0px rgba(0,0,0,0.23);

`;

export const Form = style.form`
        background: #fff;
        padding:25px;
        border-radius:5px;
    `;
