import styled from 'styled-components'


export const Container = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    height: 100vh;
`

export const Div = styled.div`
  flex-basis: 420px;

`

export const Form = styled.form`
    width: 100%;
    background: rgb(238, 176, 207);
    padding: 20px;
    border-radius: 5px;
    box-shadow: 0px 4px 2px 0px rgba(0, 0, 0, 0.1);
`
export const InputField = styled.input`
    width: 100%;
    box-sizing: border-box;
    padding: 1em;
    margin:8px 0px;
    border-radius: 3px;;
    border: 1px solid lightgrey;
    background: ${ (props) => props ? 'teal' : 'teal' };
`;

export const DivError = styled.span`
   color: red;
    font-size: small;
    text-align: right;
    position: relative;
    span{
        width: 100%;
    display: block;
    }
`

export const Button = styled.button`
    width: 100%;
    box-sizing: border-box;
    padding: 1em;
    margin:8px 0px;
    border-radius: 3px;;
    border: 1px solid lightgrey;
    text-transform: uppercase;

`
export const Terms = styled.p`
    font-size: small;
    text-align: center;
    strong {
        color: red;
    }
`

export const IconFiled = styled.span`
    background-color: brown;
    position: absolute;
    left: -40px;
    top: -5px;


`
